DROP TABLE IF EXISTS `t_sys_permission`;

CREATE TABLE `t_sys_permission`  (
  `permissionId` bigint(11) NOT NULL AUTO_INCREMENT COMMENT '权限id',
  `parentId` bigint(20) NULL DEFAULT NULL COMMENT '父权限ID，顶级权限为0',
  `permissionName` varchar(50) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT '' COMMENT '权限名称',
  `description` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT '' COMMENT '权限功能详细描述',
  `status` int(2) NULL DEFAULT 1 COMMENT '权限状态，1有效，0无效',
  `type` int(11) NULL DEFAULT NULL COMMENT '类型   0：目录   1：菜单   2：按钮',
  `createTime` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updateTime` timestamp(0) NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  `defaultPermission` int(2) NULL DEFAULT 0 COMMENT '是否为默认权限，0否，1是',
  `version` int(11) NULL DEFAULT 0 COMMENT '版本号',
  `url` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT '' COMMENT '权限url地址，可以为空',
  `perms` varchar(500) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '授权(多个用逗号分隔，如：user:list,user:create)',
  `icon` varchar(50) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '菜单栏icon',
  `seq` int(2) NULL DEFAULT NULL,
  PRIMARY KEY (`permissionId`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 93 CHARACTER SET = utf8 COLLATE = utf8_bin ROW_FORMAT = Dynamic;


DELETE
FROM
	t_sys_role_permission_rel
WHERE
	permissionId NOT IN ( SELECT permissionId FROM t_sys_permission WHERE `status` = 1 )